<?php/** * * 화면출력 * * 화면출력에 필요한 선처리 그리고 레이아웃 불러오기 * * @param string $VIEW['head']['meta']['title'] * @param string $VIEW['head']['meta']['type'] * @param string $VIEW['head']['meta']['url'] * @param string $VIEW['head']['meta']['image'] * @param string $VIEW['head']['meta']['description'] * */if(!defined('__MAPC__')) { exit(); }{ // BLOCK:module_config:20130102:필요한 모듈의 환경설정 불러오기, include required module config	// 메뉴 모듈	include_once(MODULE_PATH . 'menu/config/config.php');	// 사용자 모듈	include_once(MODULE_PATH . 'user/config/config.php');} // BLOCK{ // BLOCK:menu_get:20121228:get menu array and convert to <li> type	include_once($PATH['menu']['root'] . 'model/menu_convert.func.php');	{ // 메인메뉴 : 머리		if($ARGS['admin']) {            @include_once(CONFIG_PATH . 'menu.admin.php');            @include_once($PATH['menu']['data'] . $_SESSION['mapc_user_uid'] . '/admin_menu.php');			$menu	= $MENU['admin'];		} else {			// #TODO 메뉴화일 불러다가 $MENU['_time']값을 기준으로 새로운 화일인지 체크            @include_once(CONFIG_PATH . 'menu.main.php');            @include_once($PATH['menu']['data'] . $_SESSION['mapc_user_uid'] . '/site_menu.php');			$menu	= $MENU['home'];		}		$option	= array(			'submenu'	=> true,            'dropdown'  => true,			'menu_class'=> 'nav navbar-nav',			'page_current'	=> 'home'		);		$VIEW['head']['menu']	= module_menu_convert($menu, $option);        unset($menu);	} // 메인메뉴 : 꼬리	{ // 서브메뉴 : 머리		// 서브모듈 별로 서브메뉴출력		$temp_modl = $ARGS['modl'] ? $ARGS['modl'] : $MENU['_default_submenu'];		if($ARGS['admin']) {			$menu = $MENU['admin'][$temp_modl];		} else {			$menu = $MENU['sitemap'][$temp_modl];		}		$option	= array(			'submenu'	=> TRUE,			'menu_class'=> 'nav nav-list',		);		$VIEW['head']['menu_sub']	= module_menu_convert($menu, $option);        unset($menu);	} // 메인메뉴 : 꼬리} // BLOCK{ // BLOCK:set_layout:20150828    $CONFIG['layout'] = $ARGS['admn'] ? $CONFIG['layout_admin'] : $CONFIG['layout'];    // admin일 경우 다른 레이아웃을 사용할 때...} // BLOCK{ // BLOCK:meta_handle:20131224:메타데이터 처리    $VIEW['head']['js']['jquery.min.js']    = $URL['core']['root'] . 'vendor/jquery/dist/';    $VIEW['head']['js']['bootstrap.min.js'] = $URL['core']['root'] . 'vendor/bootstrap/dist/js/';    $VIEW['head']['css']['bootstrap.min.css']   = $URL['core']['root'] . 'vendor/bootstrap/dist/css/';    $VIEW['headhook']['headhook_metatag.tpl.php'] = LAYOUT_PATH . $CONFIG['layout'] . '/';    if($_SESSION['mapc_user_id']) {        $VIEW['sign_in_out']      = _('로그아웃');        $VIEW['sign_in_out_link'] = $URL['core']['root'] . 'user/sign_out/';        $VIEW['user_profile']     = _('내정보');        $VIEW['user_profile_link']= $URL['core']['root'] . 'user/edit/';    } else {        $VIEW['sign_in_out']      = _('로그인');        $VIEW['sign_in_out_link'] = $URL['core']['root'] . 'user/sign_in/';        $VIEW['user_profile']     = _('회원가입');        $VIEW['user_profile_link']= $URL['core']['root'] . 'user/sign_up/';    }} // BLOCK// end of file